// src/components/Slideshow.js
import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css"; 

const Slideshow = () => {
  const settings = {
    dots: true, // Enables dots navigation
    infinite: true, // Infinite loop sliding
    speed: 500, // Transition speed
    slidesToShow: 1, // Number of slides to show at once
    slidesToScroll: 1, // Number of slides to scroll at once
    autoplay: true, // Auto-slide feature
    autoplaySpeed: 3000, // 3 seconds per slide
  };

  return (
    <div className="w-full max-w-6xl mx-auto mt-8 p-4">
      <Slider {...settings}>
        <div>
          <img 
            src="https://images.unsplash.com/photo-1565373533185-6a5d6674a33a?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80" 
            alt="Repairing Suitcases" 
            className="w-full h-64 md:h-96 object-cover rounded-lg"
          />
        </div>
        <div>
          <img 
            src="https://images.unsplash.com/photo-1598136495970-5e5e480cd247?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80" 
            alt="Technicians at Work" 
            className="w-full h-64 md:h-96 object-cover rounded-lg"
          />
        </div>
        <div>
          <img 
            src="https://images.unsplash.com/photo-1604328698696-027f51e469b6?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80" 
            alt="Customer Satisfaction" 
            className="w-full h-64 md:h-96 object-cover rounded-lg"
          />
        </div>
      </Slider>
    </div>
  );
};

export default Slideshow;
