import React from "react";

const About = () => {
    return(
        <div>
             {/* About Us Section */}
      <section className="py-12 px-4 bg-gray-100 text-center" id="about">
        <h2 className="text-3xl font-bold text-gray-800">About Us</h2>
        <p className="mt-4 text-gray-600 text-lg max-w-3xl mx-auto">
          At Alineed Solutions, we specialize in high-quality suitcase repairs. Our services restore your luggage to its best condition. With years of experience, we are committed to delivering excellence to our customers.
        </p>
      </section>
        </div>

    )
}
export default About
