// src/components/Slideshow.js
import React from 'react';
import logo from '../assests/logo.png'
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css"; 

const Slideshow = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="w-full max-w-6xl mx-auto mt-8 p-4"> {/* Reduced max-width */}
      <Slider {...settings}>
        <div>
          <img
            src="https://images.pexels.com/photos/5598288/pexels-photo-5598288.jpeg?auto=compress&cs=tinysrgb&w=600" 
            alt="Repairing Suitcases" 
            className="w-full h-64 md:h-96 object-fill rounded-lg"
          />
        </div>
        <div>
          <img 
            src="https://images.unsplash.com/photo-1598136495970-5e5e480cd247?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80" 
            alt="Technicians at Work" 
            className="w-full h-64 md:h-96 object-contain rounded-lg"
          />
        </div>
        <div>
          <img 
            src="https://images.unsplash.com/photo-1604328698696-027f51e469b6?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80" 
            alt="Customer Satisfaction" 
            className="w-full h-64 md:h-96 object-contain rounded-lg"
          />
        </div>
      </Slider>
    </div>
  );
};

export default Slideshow;
